  LinearProgress,
  Chip,
} from '@mui/material';
import {
  Assignment,
  Folder,
  People,
  TrendingUp,
  Schedule,
  CheckCircle,
  Warning,
  Error,
} from '@mui/icons-material';
import { projectsService, tasksService, usersService } from '../services/apiService';
import { Project, Task, User, TaskStats } from '../types';

const Dashboard: React.FC = () => {
  const [projects, setProjects] = useState<Project[]>([]);
  const [tasks, setTasks] = useState<Task[]>([]);
  const [users, setUsers] = useState<User[]>([]);
  const [taskStats, setTaskStats] = useState<TaskStats | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      const [projectsData, tasksData, usersData, statsData] = await Promise.all([
        projectsService.getAll(),
        tasksService.getAll(),
        usersService.getAll(),
        tasksService.getStats(),
      ]);

      setProjects(projectsData);
      setTasks(tasksData);
      setUsers(usersData);
      setTaskStats(statsData);
    } catch (err: any) {
      setError(err.response?.data?.error || 'Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'critical': return 'error';
      case 'high': return 'warning';
      case 'medium': return 'info';
      case 'low': return 'success';
      default: return 'default';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'done': return 'success';
      case 'in_progress': return 'info';
      case 'review': return 'warning';
      case 'todo': return 'default';
      default: return 'default';
    }
  };

  const StatCard: React.FC<{
    title: string;
    value: number;
    icon: React.ReactNode;
    color: string;
    subtitle?: string;
  }> = ({ title, value, icon, color, subtitle }) => (
    <Paper sx={{ p: 3, textAlign: 'center', height: '100%' }}>
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', mb: 2 }}>
        <Box sx={{ color, mr: 1 }}>
          {icon}
        </Box>
        <Typography variant="h4" color={color}>
          {value}
        </Typography>
      </Box>
      <Typography variant="h6" gutterBottom>
        {title}
      </Typography>
      {subtitle && (
        <Typography variant="body2" color="text.secondary">
          {subtitle}
        </Typography>
      )}
    </Paper>
  );

  const RecentTasks: React.FC = () => {
    const recentTasks = tasks
      .sort((a, b) => new Date(b.created_at).getTime() - new Date(a.created_at).getTime())
      .slice(0, 5);

    return (
      <Card>
        <CardContent>
          <Typography variant="h6" gutterBottom>
            Recent Tasks
          </Typography>
          {recentTasks.map((task) => (
            <Box key={task.id} sx={{ display: 'flex', alignItems: 'center', mb: 2, p: 1, borderRadius: 1, '&:hover': { bgcolor: 'action.hover' } }}>
              <Box sx={{ flex: 1 }}>
                <Typography variant="subtitle2">
                  {task.title}
                </Typography>
                <Typography variant="caption" color="text.secondary">
                  {task.project_name} • {task.assigned_to_name || 'Unassigned'}
                </Typography>
              </Box>
              <Box sx={{ display: 'flex', gap: 1 }}>
                <Chip
                  label={task.priority}
                  color={getPriorityColor(task.priority) as any}
                  size="small"
                />
                <Chip
                  label={task.status.replace('_', ' ')}
                  color={getStatusColor(task.status) as any}
                  size="small"
                />
              </Box>
            </Box>
          ))}
        </CardContent>
      </Card>
    );
  };

  const ProjectProgress: React.FC = () => {
    const activeProjects = projects.filter(p => p.status === 'active');

    return (
      <Card>
        <CardContent>
          <Typography variant="h6" gutterBottom>
            Project Progress
          </Typography>
          {activeProjects.map((project) => {
            const progress = project.total_tasks ? (project.completed_tasks || 0) / project.total_tasks * 100 : 0;
            return (
              <Box key={project.id} sx={{ mb: 2 }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                  <Typography variant="subtitle2">
                    {project.name}
                  </Typography>
                  <Typography variant="caption" color="text.secondary">
                    {Math.round(progress)}%
                  </Typography>
                </Box>
                <LinearProgress
                  variant="determinate"
                  value={progress}
                  sx={{ height: 6, borderRadius: 3 }}
                />
                <Typography variant="caption" color="text.secondary">
                  {project.completed_tasks || 0}/{project.total_tasks || 0} tasks completed
                </Typography>
              </Box>
            );
          })}
        </CardContent>
      </Card>
    );
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Dashboard
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      <Grid container spacing={3}>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Total Projects"
            value={projects.length}
            icon={<Folder />}
            color="primary.main"
            subtitle={`${projects.filter(p => p.status === 'active').length} active`}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Total Tasks"
            value={tasks.length}
            icon={<Assignment />}
            color="secondary.main"
            subtitle={taskStats ? `${taskStats.done_tasks} completed` : ''}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Team Members"
            value={users.length}
            icon={<People />}
            color="success.main"
            subtitle={`${users.filter(u => u.role === 'admin').length} admins`}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Overdue Tasks"
            value={taskStats?.overdue_tasks || 0}
            icon={<Schedule />}
            color="error.main"
            subtitle="Needs attention"
          />
        </Grid>
      </Grid>

      {taskStats && (
        <Grid container spacing={3} sx={{ mt: 2 }}>
          <Grid item xs={12} md={6}>
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" gutterBottom>
                Task Status Overview
              </Typography>
              <Grid container spacing={2}>
                <Grid item xs={6}>
                  <Box textAlign="center">
                    <CheckCircle color="success" sx={{ fontSize: 40 }} />
                    <Typography variant="h4" color="success.main">
                      {taskStats.done_tasks}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Completed
                    </Typography>
                  </Box>
                </Grid>
                <Grid item xs={6}>
                  <Box textAlign="center">
                    <Warning color="warning" sx={{ fontSize: 40 }} />
                    <Typography variant="h4" color="warning.main">
                      {taskStats.in_progress_tasks}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      In Progress
                    </Typography>
                  </Box>
                </Grid>
                <Grid item xs={6}>
                  <Box textAlign="center">
                    <Assignment color="info" sx={{ fontSize: 40 }} />
                    <Typography variant="h4" color="info.main">
                      {taskStats.todo_tasks}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      To Do
                    </Typography>
                  </Box>
                </Grid>
                <Grid item xs={6}>
                  <Box textAlign="center">
                    <Error color="error" sx={{ fontSize: 40 }} />
                    <Typography variant="h4" color="error.main">
                      {taskStats.overdue_tasks}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Overdue
                    </Typography>
                  </Box>
                </Grid>
              </Grid>
            </Paper>
          </Grid>
          <Grid item xs={12} md={6}>
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" gutterBottom>
                Priority Distribution
              </Typography>
              <Grid container spacing={2}>
                <Grid item xs={6}>
                  <Box textAlign="center">
                    <Typography variant="h4" color="error.main">
                      {taskStats.critical_tasks}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Critical
                    </Typography>
                  </Box>
                </Grid>
                <Grid item xs={6}>
                  <Box textAlign="center">
                    <Typography variant="h4" color="warning.main">
                      {taskStats.high_tasks}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      High Priority
                    </Typography>
                  </Box>
                </Grid>
              </Grid>
            </Paper>
          </Grid>
        </Grid>
      )}

      <Grid container spacing={3} sx={{ mt: 2 }}>
        <Grid item xs={12} md={6}>
          <RecentTasks />
        </Grid>
        <Grid item xs={12} md={6}>
          <ProjectProgress />
        </Grid>
      </Grid>
    </Box>
  );
};

export default Dashboard;
